    render() {
        const header = ['Title','Genre','Stock','Rate'];
        return (
            <table class="table">
                <thead>
                    <tr>
                        {header.map(title => <th scope='col'>{title}</th>)}
                    </tr>
                </thead>
                <tbody>
                <tr>
                {this.state.movie.map(movie=>
                     <td>{saveMovie(movie)}</td>)                        
                }
                 </tr>
                </tbody>
            </table>
        );

    }
}
export default Movie;
