import React, { Component } from "react";
import Like from "./common/like";
import TableHeader from "./common/tabeHeader";

class MoviesTable extends React.Component {
  render() {
    const { movie, onHandleClick, onHandleDelete, sortColumn } = this.props;
    const columns = ["Title", "Genre", "Stock", "Rate"];
    return (
      <table className="table align-middle">
        <TableHeader columns={columns} sortColumn={sortColumn} movie={movie} />
        <tbody>
          {movie.map((movie) => (
            <tr key={movie._id}>
              <td>{movie.title}</td>
              <td>{movie.genre.name}</td>
              <td>{movie.numberInStock}</td>
              <td>{movie.dailyRentalRate}</td>
              <td>
                <Like
                  isClick={movie.isClick}
                  onClick={() => onHandleClick(movie)}
                />
              </td>
              <td>
                <button
                  onClick={() => onHandleDelete(movie)}
                  className="btn btn-danger btn-sm m-2"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
