import React, { Component, useState } from "react";
import { getMovies } from "../services/fakeMovieService";
import Pagnition from "./pagnition";
import { paginate } from "../utils/paginate";
import Like from "./common/like";
import ListGroup from "./listgroup";
import { getGenres } from "../services/fakeGenreService";

class Movie extends Component {
  state = {
    movies: getMovies(),
    pageSize: 4,
    currentPage: 1,
    genre: getGenres(),
  };
  handleDelete = (movie) => {
    const movies = this.state.movies.filter((m) => m._id !== movie._id);
    this.setState({ movies: movies });
  };
  handleClick = (movie) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(movie);
    movies[index] = { ...movie };
    movies[index].isClick = !movies[index].isClick;
    this.setState({ movies: movies });
  };
  handlePageChange = (page) => {
    this.setState({ currentPage: page });
  };

  render() {
    const header = ["Title", "Genre", "Stock", "Rate"];
    const { length: count, genre } = this.state.movies;
    const { pageSize, currentPage, movies } = this.state;
    const { name, _id, length: genreCount } = this.state.genre;

    if (count === 0) return <p>There is not any movie in database</p>;

    const movie = paginate(movies, currentPage, pageSize);

    return (
      <React.Fragment>
        {console.log(this.state.movies.genre.name)}
        <div>
          <ListGroup key={_id} genreCount={genreCount} genreName={name} />
        </div>

        <p>Number of movies: {count}</p>
        <table className="table align-middle">
          <thead>
            <tr>
              {header.map((title, index) => (
                <th key={index}>{title}</th>
              ))}
              <th>Favorite</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {movie.map((movie) => (
              <tr key={movie._id}>
                <td>{movie.title}</td>
                <td>{movie.genre.name}</td>
                <td>{movie.numberInStock}</td>
                <td>{movie.dailyRentalRate}</td>
                <td>
                  <Like
                    isClick={movie.isClick}
                    onClick={() => this.handleClick(movie)}
                  />
                </td>
                <td>
                  <button
                    onClick={() => this.handleDelete(movie)}
                    className="btn btn-danger btn-sm m-2"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagnition
          itemCount={count}
          pageSize={pageSize}
          onPageChange={this.handlePageChange}
          currentPage={currentPage}
        />
      </React.Fragment>
    );
  }
}

export default Movie;
