import React, { Component } from 'react';
import {getMovies,} from '../services/fakeMovieService'

class Movie extends Component {
    state = {
        movies: getMovies(),
        deletedMovies:[]
    };
    trashBin = (movie) =>  {
        let trashCollector =[];
        return trashCollector.push(this.state.movies.filter( m => m._id === movie._id));}
    handleDelete = (movie) =>{
        const trashBin = this.trashBin(movie);
       const movies = this.state.movies.filter( m => m._id !== movie._id);
       this.setState({deletedMovies: trashBin});
       this.setState({movies:movies});
      
    }

    
    render() {
        const header = ['Title','Genre','Stock','Rate'];
        const {length:count} = this.state.movies;
        if(count===0) return <p>There is not any movie in database</p>

        return (
            <><p>Number of movies: {count}</p>
            <table className="table">
                <thead>
                    <tr>
                        {header.map((title, index) => <th key={index} scope='col'>{title}</th>)}
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {this.state.movies.map(movie => (
                        <tr key={movie._id}>
                            <td>{movie.title}</td>
                            <td>{movie.genre.name}</td>
                            <td>{movie.numberInStock}</td>
                            <td>{movie.dailyRentalRate}</td>
                            <td><button onClick={() => this.handleDelete(movie)} className='btn btn-danger btn-sm m-2'>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <p>Deleted movie</p>
            <p>{this.state.deletedMovies.length=== 0 && 'There is nothing here' }</p>
            <table className='table'>
                <thead>
                    <tr>
                        {this.state.length > 0 && (header.map((title, index) => <th key={index} scope='col'>{title}</th>))}
                        <th></th>
                    </tr>      
                </thead>
                <tbody>
                    {console.log(this.state)}
                </tbody>
            </table> )
            </>
       ) ;
    }
}

export default Movie;
