import React, { Component } from 'react';
import {getMovie} from '../services/fakeMovieService'

class Movie extends Component {
    state = {
        movie: getMovie()
    };

    render() {
        const header = ['Title','Genre','Stock','Rate'];
        return (
            <table class="table">
                <thead>
                    <tr style = {{fontSize :40,fontWeight: 'bold'}}>
                        {header.map((title,index) => <th scope='col'>{title}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {this.state.movie.map(component=>{
                    return(<tr>
                        <td>{component.title}</td>
                        <td>{component.genre.name}</td>
                        <td>{component.numberInStock}</td>
                        <td>{component.dailyRentalRate}</td>
                    </tr> )                       
                    })}
                </tbody>
            </table>
        );

    }
}
export default Movie;
