    state = {
        movie: getMovie()
    };

    render() {
        const header = ['Title','Genre','Stock','Rate'];
        return (
            <table>
                <thead>
                    <tr style = {{fontSize :40,fontWeight: 'bold'}}>
                        {header.map((title,index) => <th key={movie[index]._id}className='badge m-15'>{title}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {this.movie}
                </tbody>
            </table>
        );

    }
}
export default Movie;
