import React, { Component } from 'react';
import _ from 'lodash';

class Pagnition extends Component
{
    render()
    {
        const { itemCount, pageSize, currentPage, onPageChange } = this.props
        const pageCount = itemCount / pageSize;
        const pages = _.range(1, pageCount + 1);
        return <div>
            <nav>
                <ul className="pagination">
                    {pages.map(page => <li className={page === currentPage ? "page-item active" : "page-item"}>
                        <a className="page-link" onClick={() => onPageChange(page)}>{page}</a></li>)}
                </ul>
            </nav>
        </div>;
    }
}

export default Pagnition;
