import React, { Component } from "react";
import TableHeader from "./common/tabeHeader";
import TableBody from "./common/tableBody";
import Like from "./common/like";
class MoviesTable extends React.Component {
  columns = [
    { label: "Title", path: "title" },
    { label: "Genre", path: "genre.name" },
    { label: "Stock", path: "numberInStock" },
    { label: "Rate", path: "dailyRentalRate" },
    {
      key: "like",
      content: (movie) => (
        <Like
          isClick={movie.isClick}
          onClick={() => this.props.onHandleClick(movie)}
        />
      ),
    },
    {
      key: "delete",
      content: (movie) => (
        <button
          onClick={() => this.props.onHandleDelete(movie)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
      ),
    },
  ];
  render() {
    const { movie, sortColumn, onSort } = this.props;

    return (
      <table className="table align-middle">
        <TableHeader
          columns={this.columns}
          sortColumn={sortColumn}
          movie={movie}
          onSort={onSort}
        />
        <TableBody data={movie} columns={columns} />
      </table>
    );
  }
}

export default MoviesTable;
